mapper.select:
SELECT MAX(REVIEW_ID)
FROM REVIEW
WHERE RESERVATION_ID = #{reservation_id}

mapper.delete:
DELETE REVIEW_IMAGE 
WHERE REVIEW_ID = #{review_id}
AND REVIEW_IMG_ID = #{review_img_id}

mapper.update:
UPDATE REVIEW 
SET REVIEW_CONTENT = #{review_content}
		,GRADE = #{grade}
WHERE REVIEW_ID = #{review_id}
